from modules.InitializeSystem import create_directories
from modules.DatabaseQueries import create_database, insert_user_to_database
from modules.ApiCalls import register_company_api_call
from modules.ConfigureJsonFile import create_url_json_file, create_json_file, create_runtime_json_file
import numpy as np


def register_company(input_company_name, foreground_color, background_color, deep_color, email, password, first_name, last_name, encodings, register_code):
    create_url_json_file()
    create_runtime_json_file()
    success, company_details, user_details = register_company_api_call(input_company_name, foreground_color, background_color, deep_color, email, password, first_name, last_name, encodings, register_code)
    if success:
        create_directories()
        create_json_file(*company_details)
        create_database()
        insert_user_to_database(*user_details)
        return True
    return False


